    console.log(category)

    // Pick random word
    const word = words[category][Math.floor(Math.random() * words[category].length)]
    console.log(word)
  }

  // Navigation
  const startGame = () => {
    pickWordAndCategory()

    setGameStage(stages[1].name)
  }
  const backHome = () => {
    setGameStage(stages[0].name)
  }
  const endScreen = () => {
    setGameStage(stages[2].name)
  }
  const retry = () => {
    setGameStage(stages[1].name)
  }

  // Process the letter input


  return (
    <div className="App">
      {gameStage === 'start' && <StartScreen startGame={startGame} />}
      {gameStage === 'game' && <Game backHome={backHome} endScreen={endScreen} />}
      {gameStage === 'end' && <GameOver retry={retry} backHome={backHome} />}
    </div>
  );
}

export default App;
